const {
    click,
    press,
    write
  } = require("taiko");

export const select = async (option, label, selector) => {
    await click(label, selector);
    await press("Tab");
    await press("Tab");
    await write(option);
    await press("Enter");
  };
  
export const writeSelect = async (option, label) => {
    await click(label);
    await press("Tab");
    await press("Tab");
    await write(option);
    await press("Enter");
  };